                {
                    if (act.ToUpper() == "SCHLFNL")
                    {

                        var data = _context.StudentPreviousYearMarks.Where(s => s.SCHL == id.ToString()).ToList();
                        ViewBag.IsFinalLockSubmitted = "0";
